<?php get_header(); ?><?php          $theme_locations = get_nav_menu_locations();    $menu_obj = get_term( $theme_locations['primary'], 'nav_menu' );    //the registered menu    $menu_name = $menu_obj->name;        $menu_items = wp_get_nav_menu_items( $menu_name );    $menu_items_ids = array();     foreach ( $menu_items as $item ) {        if ( $item->object == 'page' && !$item->menu_item_parent ) {            $menu_items_ids[] = $item->object_id;        }    }    $args = array(	'sort_order' => 'ASC',	'sort_column' => 'menu_order',	'include' => $menu_items_ids    );    $pages = get_pages($args);        // The Loop    if ( $pages ) :        foreach ( $pages as $page ) : ?>            <div class="row">                <div class="box article full-width">                    <h2 data-magellan-destination="<?php echo $page->ID; ?>" class="title"><?php echo $page->post_title; ?></h2>                    <a name="<?php echo $page->ID; ?>"></a>                     <?php echo wpautop($page->post_content); ?>                </div>            </div>        <?php endforeach;    else :        include_once 'parts/no-post.php';    endif;    /* Restore original Post Data */    wp_reset_postdata();?><?php get_footer(); ?>